mapper.select:
SELECT c.customer_id customerId , c.customer_name customerName , (SELECT COUNT(*) FROM booking WHERE customer_id = c.customer_id AND (booking_state = '예약확정' OR booking_state = '방문완료') AND booking_company_id=#{loginCompanyId}) bookingCount , (SELECT COUNT(*) FROM booking WHERE customer_id = c.customer_id AND (booking_state = '취소') AND booking_company_id=#{loginCompanyId}) cancelCount , (SELECT COUNT(*) FROM booking b INNER JOIN review r ON b.booking_no = r.booking_no WHERE b.customer_id = c.customer_id AND b.booking_company_id=#{loginCompanyId}) reviewCount , IFNULL((SELECT SUM(booking_price) FROM booking WHERE customer_id = c.customer_id AND (booking_state = '예약확정' OR booking_state = '방문완료') AND booking_company_id=#{loginCompanyId}), 0) bookingTotalPrice , c.customer_rank customerRank FROM customer c INNER JOIN booking b ON c.customer_id = b.customer_id INNER JOIN total_id ti ON c.customer_id = ti.id WHERE b.booking_company_id = #{loginCompanyId} AND ti.total_id_deactive_memo IS NULL GROUP BY c.customer_id
bookingCount ASC bookingCount DESC cancelCount ASC cancelCount DESC reviewCount ASC reviewCount DESC bookingTotalPrice ASC bookingTotalPrice DESC
LIMIT #{beginRow}, #{rowPerPage}
